void fault(uint64_t *tree, int fault_tree)
{

    int number = pow(2, SPX_TREE_HEIGHT) - 1;
    // number = 1;
    // printf("tree height = %d, %d",SPX_TREE_HEIGHT, number);

    if (fault_tree)
    {
        *tree = *tree & ~((uint64_t)number << (SPX_TREE_HEIGHT * (SPX_D - 5)));
        // *tree = *tree & ~ ((uint64_t)number << (SPX_TREE_HEIGHT));
        // *tree = *tree & ~((uint64_t)number << 5);
    }
}

static void gen_chain_test(unsigned char *out, const unsigned char *in,
                           unsigned int start, unsigned int steps,
                           const spx_ctx *ctx, uint32_t addr[8])
{
    uint32_t i;

    /* Initialize out with the value at position 'start'. */
    memcpy(out, in, SPX_N);

    /* Iterate 'steps' calls to the hash function. */
    for (i = start; i < (start + steps) && i < SPX_WOTS_W; i++)
    {
        set_hash_addr(addr, i);
        thash(out, out, 1, ctx, addr);
    }
}

void wots_forge_test(unsigned char *pk, unsigned int lengths[SPX_WOTS_LEN],
                     const unsigned char *known_values, const unsigned char *root_msg,
                     const spx_ctx *ctx, uint32_t addr[8])
{
    uint32_t i;
    unsigned int lengths_out[SPX_WOTS_LEN];
    chain_lengths(lengths_out, root_msg);

    for (i = 0; i < SPX_WOTS_LEN; i++)
    {
        set_chain_addr(addr, i);
        gen_chain_test(pk + i * SPX_N, known_values + i * SPX_N,
                       lengths[i], lengths_out[i] - lengths[i], ctx, addr);
    }
}

void find_bi_values(unsigned int lengths[SPX_WOTS_LEN], const unsigned char *wots_sig,
                    const unsigned char *wots_pk, const spx_ctx *ctx, uint32_t addr[8])
{
    // FILE *file = fopen("thashes.txt", "w"); 
    unsigned char out[SPX_WOTS_BYTES];
    int i;
    for (i = 0; i < SPX_WOTS_LEN; i++)
    {   
        for(int j = 0; j < SPX_WOTS_W; j++){
            // printf("for i = %d, j = %d\n", i,j);
            set_chain_addr(addr, i);
            gen_chain_test(out + i * SPX_N, wots_sig + i * SPX_N,
                       j, SPX_WOTS_W - 1 - j, ctx, addr);
            // fprintf(file, "i = %d, j = %d : ", i,j);
            // fprintbstr(file, "", out + i * SPX_N, SPX_N);
            // fprintbstr(file, "", wots_pk + i * SPX_N, SPX_N);
            if(memcmp(out + i * SPX_N, wots_pk + i * SPX_N, SPX_N) == 0){
                // printf("for %d chain, bi = %d\n",i,j);
                lengths[i] = j;
                break;
            }
        }
    }
}

int controlled_merkle_sign(uint8_t *sig, unsigned char root[SPX_N], unsigned char *source,
                           unsigned int lengths[SPX_WOTS_LEN], uint32_t layer, uint64_t tree,
                           uint32_t idx_leaf, const uint8_t *sk)
{
    spx_ctx ctx;
    const unsigned char *pk = sk + 2 * SPX_N;
    uint32_t wots_addr[8] = {0};
    uint32_t tree_addr[8] = {0};

    memcpy(ctx.sk_seed, sk, SPX_N);
    memcpy(ctx.pub_seed, pk, SPX_N);

    initialize_hash_function(&ctx);

    set_type(wots_addr, SPX_ADDR_TYPE_WOTS);
    set_type(tree_addr, SPX_ADDR_TYPE_HASHTREE);

    set_tree_addr(wots_addr, tree);
    set_keypair_addr(wots_addr, idx_leaf);

    set_layer_addr(tree_addr, layer);
    set_tree_addr(tree_addr, tree);

    copy_subtree_addr(wots_addr, tree_addr);
    set_keypair_addr(wots_addr, idx_leaf);

    wots_forge_test(sig, lengths, source, root, &ctx, wots_addr);

    return 0;
}

int  crypto_sign_signature_test(uint8_t *sig, size_t *siglen,
                               const uint8_t *m, size_t mlen, const uint8_t *sk)
{
    // FILE* file = fopen("signature_test.txt","a");
    FILE *file2 = fopen("results_each_layer.txt", "a");

    spx_ctx ctx;

    const unsigned char *sk_prf = sk + SPX_N;
    const unsigned char *pk = sk + 2 * SPX_N;

    unsigned char optrand[SPX_N];
    unsigned char mhash[SPX_FORS_MSG_BYTES];
    unsigned char root[SPX_N];
    uint32_t i;
    uint64_t tree;
    uint32_t idx_leaf;
    uint32_t wots_addr[8] = {0};
    uint32_t tree_addr[8] = {0};

    unsigned int lengths[SPX_WOTS_LEN];

    memcpy(ctx.sk_seed, sk, SPX_N);
    memcpy(ctx.pub_seed, pk, SPX_N);

    /* This hook allows the hash function instantiation to do whatever
       preparation or computation it needs, based on the public seed. */
    initialize_hash_function(&ctx);

    set_type(wots_addr, SPX_ADDR_TYPE_WOTS);
    set_type(tree_addr, SPX_ADDR_TYPE_HASHTREE);

    /* Optionally, signing can be made non-deterministic using optrand.
       This can help counter side-channel attacks that would benefit from
       getting a large number of traces when the signer uses the same nodes. */

    randombytes(optrand, SPX_N);
    fprintbstr(file2, "optrand = ", optrand, SPX_N);

    /* Compute the digest randomization value. */

    gen_message_random(sig, sk_prf, optrand, m, mlen, &ctx);

    fprintbstr(file2, "message  = ", (unsigned char *)m, mlen);
    fprintbstr(file2, "R = ", sig, SPX_N);

    /* Derive the message digest and leaf index from R, PK and M. */
    hash_message(mhash, &tree, &idx_leaf, sig, pk, m, mlen, &ctx);

    // fprintf(file, "tree = %ld, leaf = %d\n", tree, idx_leaf);
    // fprintf(file, "tree before = %ld, leaf = %d\n", tree, idx_leaf);
    // fprintbstr(file, "tree before in hex= ", (unsigned char*)&tree, sizeof(uint64_t));
    // fault(&tree, 1);
    // fprintf(file, "tree after = %ld, leaf = %d\n", tree, idx_leaf);
    // fprintbstr(file, "tree after in hex= ", (unsigned char*)&tree, sizeof(uint64_t));

    sig += SPX_N;

    set_tree_addr(wots_addr, tree);
    set_keypair_addr(wots_addr, idx_leaf);

    /* Sign the message hash using FORS. */
    fors_sign(sig, root, mhash, &ctx, wots_addr);
    fprintbstr(file2, "fors sign = ", sig, SPX_FORS_BYTES);

    fors_pk_from_sig(root, sig, mhash, &ctx, wots_addr);
    fprintbstr(file2, "fors auth = ", root, SPX_N);

    sig += SPX_FORS_BYTES;

    for (i = 0; i < SPX_D; i++)
    {
        set_layer_addr(tree_addr, i);
        set_tree_addr(tree_addr, tree);

        copy_subtree_addr(wots_addr, tree_addr);

        // fprintbstr(file2, "wots_addr before key pair = ", wots_addr, 8*(sizeof(uint32_t)));
        set_keypair_addr(wots_addr, idx_leaf);
        // fprintbstr(file2, "wots_addr after key pair  = ", wots_addr, 8*(sizeof(uint32_t)));

        fprintbstr(file2, "root = ", root, SPX_N);
        chain_lengths(lengths, root);
        fprintf(file2, "layer %d\n", i);
        fprintsteps(file2, "lengths = ", lengths, SPX_WOTS_LEN);

        // if(i!=17){merkle_sign(sig, root, &ctx, wots_addr, tree_addr, idx_leaf);}
        // if(i==17){merkle_sign(sig, root, &ctx, wots_addr, tree_addr, idx_leaf);}

        // fprintbstr(file2, "tree_addr = ", tree_addr, 8*(sizeof(uint32_t)));
        // fprintbstr(file2, "wots_addr = ", wots_addr, 8*(sizeof(uint32_t)));

        merkle_sign(sig, root, &ctx, wots_addr, tree_addr, idx_leaf);
        // fprintbstr(file2, "root_after_merkle = ",root, SPX_N);
        fprintf(file2, "tree = %ld, leaf = %d\n", tree, idx_leaf);
        fprintbstr(file2, "wots sign = ", sig, SPX_WOTS_BYTES);
        sig += SPX_WOTS_BYTES;

        fprintbstr(file2, "XMSS auth = ", sig, SPX_TREE_HEIGHT * SPX_N);
        sig += SPX_TREE_HEIGHT * SPX_N;

        /* Update the indices for the next layer. */
        idx_leaf = (tree & ((1 << SPX_TREE_HEIGHT) - 1));
        tree = tree >> SPX_TREE_HEIGHT;
    }

    // fclose(file);
    fclose(file2);
    *siglen = SPX_BYTES;

    return 0;
}

